function MainSection() {
  const [show, setShow] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const handleClose = () => {
    setShow(false);
    setEditIndex(null); // Reset the editIndex when closing the modal
  };

  const handleShow = () => setShow(true);

  const [formData, setFormData] = useState({
    email: '',
    restroomName: '',
    address: '',
    landmark: '',
    gender: ''
  });

  const [formList, setFormList] = useState([]);



  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (event) => {
    console.log('inside handleSubmit method');
    event.preventDefault();

    if (editIndex !== null) {
      // If editIndex is not null, it means we are editing an existing item
      const updatedFormList = [...formList];
      updatedFormList[editIndex] = formData;
      setFormList(updatedFormList);
      setEditIndex(null);
    } else {
      // Otherwise, it's a new item being added
      setFormList([...formList, formData]);
    }

    setFormData({
      email: '',
      restroomName: '',
      address: '',
      landmark: '',
      gender: ''
    });

    handleClose();

    // const form = event.currentTarget;
    // if (form.checkValidity() === false) {
    //   event.preventDefault();
    //   event.stopPropagation();
    // }
    console.log(formList); // Log the selected gender value

  };

  const handleEdit = (index) => {
    const itemToEdit = formList[index];
    setFormData(itemToEdit);
    setEditIndex(index);
    handleShow();

  }

  const handleDelete = (index) => {
    const newCopyList = [...formList];
    console.log('Delete clicked ' + index);
    newCopyList.splice(index, 1);
    console.log('newCopyList' + newCopyList);
    setFormList(newCopyList);
  }

  return (
    <>
      <main>
        <section class="py-5 text-center container">
          <div class="row py-lg-5">
            <div class="col-lg-6 col-md-8 mx-auto">
              <h1 class="fw-light">Public Restrooms Search</h1>
              <p class="lead text-body-secondary">Search, Add public restrooms in your city!!</p>
              <p>
                <Button variant="primary" onClick={handleShow}>
                  Add Restrooms
                </Button>

                <Modal show={show} onHide={handleClose}>
                  <Modal.Header closeButton>
                    <Modal.Title>Enter Restroom details in the form below.</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>


                    <Form onSubmit={handleSubmit}>
                      <Form.Group className="mb-3" controlId="email">
                        <Form.Label>Your email address</Form.Label>
                        <Form.Control type="email" placeholder="Enter email" name="email" value={formData.email} onChange={handleChange} />
                        <Form.Text className="text-muted">
                          We'll send you a confirmation email when this restroom details are added. we'll never share your email with anyone else.
                        </Form.Text>
                      </Form.Group>

                      <Form.Group className="mb-3" controlId="restroomName">
                        <Form.Label>Restroom Name</Form.Label>
                        <Form.Control type="text" placeholder="Name" name="restroomName" value={formData.restroomName} onChange={handleChange} />
                      </Form.Group>

                      <Form.Group className="mb-3" controlId="address">
                        <Form.Label>Address</Form.Label>
                        <Form.Control type="text" placeholder="Address" name="address" value={formData.address} onChange={handleChange} />
                      </Form.Group>

                      <Form.Group className="mb-3" controlId="landmark">
                        <Form.Label>Landmark</Form.Label>
                        <Form.Control type="text" placeholder="Landmark" name="landmark" value={formData.landmark} onChange={handleChange} />
                      </Form.Group>

                      <fieldset>
                        <Form.Group as={Row} className="mb-3" controlId="gender">
                          <Form.Label as="legend" column sm={2}>
                            Gender
                          </Form.Label>
                          <Col sm={10}>
                            <Form.Check
                              label="Male"
                              name="gender"
                              value="male"
                              type="radio"
                              id="gender"
                              checked={formData.gender === 'male'}
                              onChange={handleChange}

                            />
                            <Form.Check
                              label="Female"
                              name="gender"
                              type="radio"
                              value="female"
                              id="gender"
                              checked={formData.gender === 'female'}
                              onChange={handleChange}
                            />
                            <Form.Check
                              label="Other"
                              name="gender"
                              type="radio"
                              value="other"
                              id="gender"
                              checked={formData.gender === 'other'}
                              onChange={handleChange}
                            />
                          </Col>
                        </Form.Group>
                      </fieldset>

                      <Button variant="secondary" onClick={handleClose}>
                        Close
                      </Button>
                      <Button variant="primary" type="submit">
                        Submit
                      </Button>
                    </Form>

                  </Modal.Body>
                  <Modal.Footer>
                  </Modal.Footer>
                </Modal>
              </p>
            </div>
          </div>
        </section>
      </main>

      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>index </th>
            <th>email</th>
            <th>Restroom Name</th>
            <th>Address</th>
            <th>Landmark</th>
            <th>Gender</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {formList.map((item, index) => (
            <tr key={index}>


              <td>{index}</td>
              <td>{item.email}</td>
              <td>{item.restroomName}</td>
              <td>{item.address}</td>
              <td>{item.landmark}</td>
              <td>{item.gender}</td>
              <td>
                <Button onClick={() => handleDelete(index)} >Delete</Button>
                <Button onClick={() => handleEdit(index)}>Edit</Button>
                </td>
                </tr>

          ))}
        </tbody>
      </Table>
    </>

  );
}


